          ]
        },
        {
          "type": "number",
          "title": "Number",
          "description": "Number constant.",
          "examples": [
            15.5
          ]
        },
        {
          "$ref": "#/definitions/equalsExpression",
          "examples": [
            "=..."
          ]
        }
      ]
    },
