{
  "$schema": "https://schema.management.azure.com/schemas/2015-01-01/deploymentTemplate.json#",
  "contentVersion": "1.0.0.0",
  "parameters": {
    "name": {
      "type": "string",
      "defaultValue": "[resourceGroup().name]"
    },
    "appId": {
      "type": "string",
      "metadata": {
        "description": "Active Directory App ID, set as MicrosoftAppId in the Web App's Application Settings."
      }
    },
    "appSecret": {
      "type": "string",
      "metadata": {
        "description": "Active Directory App Password, set as MicrosoftAppPassword in the Web App's Application Settings. Defaults to \"\"."
      }
    },
    "useCosmosDb": {
      "type": "bool",
      "defaultValue": true
    },
    "useAppInsights": {
      "type": "bool",
      "defaultValue": true
    },
    "shouldCreateAuthoringResource": {
      "type": "bool",
      "defaultValue": true
    },
    "shouldCreateLuisResource": {
      "type": "bool",
      "defaultValue": true
    },
    "cosmosDbName": {
      "type": "string",
      "defaultValue": "[resourceGroup().name]"
    },
    "botId": {
      "type": "string",
      "metadata": {
        "description": "The globally unique and immutable bot ID. Also used to configure the displayName of the bot, which is mutable."
      }
    },
    "botSku": {
      "defaultValue": "F0",
      "type": "string",
      "metadata": {
        "description": "The pricing tier of the Bot Service Registration. Acceptable values are F0 and S1."
      }
    },
    "luisAuthoringKey": {
      "type": "string",
      "defaultValue": ""
    },
    "newAppServicePlanName": {
      "type": "string",
      "defaultValue": "[resourceGroup().name]",
      "metadata": {
        "description": "The name of the new App Service Plan."
      }
    },
    "newAppServicePlanSku": {
      "type": "object",
      "defaultValue": {
        "name": "S1",
        "tier": "Standard",
        "size": "S1",
        "family": "S",
        "capacity": 1
      },
      "metadata": {
        "description": "The SKU of the App Service Plan. Defaults to Standard values."
      }
    },
    "appServicePlanLocation": {
      "type": "string",
      "metadata": {
        "description": "The location of the App Service Plan."
      }
    },
    "existingAppServicePlan": {
      "type": "string",
      "defaultValue": "",
      "metadata": {
        "description": "Name of the existing App Service Plan used to create the Web App for the bot."
      }
    },
    "newWebAppName": {
      "type": "string",
      "defaultValue": "[resourceGroup().name]",
      "metadata": {
        "description": "The globally unique name of the Web App. Defaults to the value passed in for \"botId\"."
      }
    },
    "appInsightsName": {
      "type": "string",
      "defaultValue": "[resourceGroup().name]"
    },
    "location": {
      "type": "string",
      "defaultValue": "[resourceGroup().location]"
    },
    "appInsightsLocation": {
      "type": "string",
      "defaultValue": "[resourceGroup().location]"
    },
    "useStorage": {
      "type": "bool",
      "defaultValue": true
    },
    "storageAccountName": {
      "type": "string",
      "defaultValue": "[resourceGroup().name]"
    },
    "luisServiceName": {
      "type": "string",
      "defaultValue": "[concat(resourceGroup().name, '-luis')]"
    },
    "luisServiceAuthoringSku": {
      "type": "string",
      "defaultValue": "F0"
    },
    "luisServiceRunTimeSku": {
      "type": "string",
      "defaultValue": "S0"
    },
    "luisServiceLocation": {
      "type": "string",
      "defaultValue": "[resourceGroup().location]"
    }
  },
  "variables": {
    "defaultAppServicePlanName": "[if(empty(parameters('existingAppServicePlan')), 'createNewAppServicePlan', parameters('existingAppServicePlan'))]",
    "useExistingAppServicePlan": "[not(equals(variables('defaultAppServicePlanName'), 'createNewAppServicePlan'))]",
    "servicePlanName": "[if(variables('useExistingAppServicePlan'), parameters('existingAppServicePlan'), parameters('newAppServicePlanName'))]",
    "resourcesLocation": "[parameters('appServicePlanLocation')]",
    "cosmosDbAccountName": "[toLower(take(replace(parameters('cosmosDbName'), '_', ''), 31))]",
    "webAppName": "[if(empty(parameters('newWebAppName')), parameters('botId'), parameters('newWebAppName'))]",
    "siteHost": "[concat(variables('webAppName'), '.azurewebsites.net')]",
    "botEndpoint": "[concat('https://', variables('siteHost'), '/api/messages')]",
    "storageAccountName": "[toLower(take(replace(replace(parameters('storageAccountName'), '-', ''), '_', ''), 24))]",
    "LuisAuthoringAccountName": "[concat(parameters('luisServiceName'), '-Authoring')]"
  },
  "resources": [
    {
      "comments": "Create a Web App using an App Service Plan",
      "type": "Microsoft.Web/sites",
      "apiVersion": "2015-08-01",
      "location": "[variables('resourcesLocation')]",
      "kind": "functionapp",
      "name": "[variables('webAppName')]",
      "properties": {
        "name": "[variables('webAppName')]",
        "kind": "functionapp",
        "httpsOnly": true
      },
      "resources": [
        {
          "name": "appsettings",
          "type": "config",
          "apiVersion": "2015-08-01",
          "dependsOn": [
            "[concat('Microsoft.Web/Sites/', variables('webAppName'))]"
          ],
          "properties": {
            "FUNCTIONS_EXTENSION_VERSION": "~3",
            "FUNCTIONS_WORKER_RUNTIME": "dotnet",
            "APPINSIGHTS_INSTRUMENTATIONKEY": "[reference(resourceId('microsoft.insights/components/', parameters('appInsightsName')), '2015-05-01').InstrumentationKey]",
            "MicrosoftAppId": "[parameters('appId')]",
            "MicrosoftAppPassword": "[parameters('appSecret')]"
          }
        }
      ]
    },
    {
      "comments": "CosmosDB for bot state.",
      "type": "Microsoft.DocumentDB/databaseAccounts",
      "kind": "GlobalDocumentDB",
      "apiVersion": "2015-04-08",
      "name": "[variables('cosmosDbAccountName')]",
      "location": "[parameters('location')]",
      "properties": {
        "databaseAccountOfferType": "Standard",
