    console.log(chalk.red(msg.message));
  } else if (msg.status === BotProjectDeployLoggerType.PROVISION_ERROR_DETAILS) {
    console.log(chalk.white(msg.message));
  } else {
    console.log(chalk.green(msg.message));
  }
};

