{
  "$schema": "https://schema.management.azure.com/schemas/2015-01-01/deploymentTemplate.json#",
  "contentVersion": "1.0.0.0",
  "parameters": {
    "name": {
      "type": "string"
    },
    "dockerRegistryPassword": {
      "type": "string"
    }
  },
  "variables": {
    "location": "[resourceGroup().location]",
    "planName": "[concat(parameters('name'), '-linux-plan')]",
    "keyVaultName": "[toLower(take(replace(replace(concat(parameters('name'), 'kv'), '-', ''), '_', ''), 24))]"
  },
  "resources": [
    {
      "comments": "Linux App Service Plan to host the Composer",
      "type": "Microsoft.Web/serverfarms",
      "apiVersion": "2018-11-01",
      "name": "[variables('planName')]",
      "location": "[variables('location')]",
      "sku": {
        "tier": "Basic",
        "name": "B1"
      },
      "kind": "linux",
      "properties": {
        "reserved": true
      }
    },
    {
      "comments": "Web App to host the Composer Docker container",
      "type": "Microsoft.Web/sites",
      "apiVersion": "2018-11-01",
      "name": "[parameters('name')]",
      "location": "[variables('location')]",
      "dependsOn": [
        "[resourceId('Microsoft.Web/serverfarms', variables('planName'))]"
      ],
      "identity": {
        "type": "SystemAssigned"
      },
      "properties": {
        "serverFarmId": "[resourceId('Microsoft.Web/serverfarms', variables('planName'))]",
        "siteConfig": {
          "appSettings": [
            {
              "name": "DOCKER_ENABLE_CI",
              "value": "true"
            },
            {
              "name": "DOCKER_REGISTRY_SERVER_PASSWORD",
              "value": "[parameters('dockerRegistryPassword')]"
            },
            {
              "name": "DOCKER_REGISTRY_SERVER_URL",
              "value": "https://vfchatbot.azurecr.io"
            },
            {
              "name": "DOCKER_REGISTRY_SERVER_USERNAME",
              "value": "vfchatbot"
            },
            {
              "name": "KEYVAULT_NAME",
              "value": "[variables('keyVaultName')]"
            },
            {
              "name": "WEBSITES_ENABLE_APP_SERVICE_STORAGE",
              "value": "false"
            },
            {
              "name": "WEBSITES_PORT",
              "value": "5000"
            }
          ],
          "linuxFxVersion": "DOCKER|vfchatbot.azurecr.io/composer/web:develop",
          "alwaysOn": true
        }
      }
    }
  ]
}

